import React, { useState, useEffect } from "react";
import axios from "axios";
import Form from './components/Search';
import Show from './components/Show';
import Crypto from "./models/crypto";

function App() {
  const [cryptocurrency, setCryptocurrency] = useState<Crypto[]> ();
  const [search, setSearch] =useState('');

  useEffect(() => {
    axios
      .get(
        "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false"
      )
      .then((res) => {
        setCryptocurrency(res.data);
      })
      .catch(error => console.log("Error"));
  },[]);



  if(cryptocurrency){
    const filteredCrypto: Crypto[] = cryptocurrency.filter(cryptocurrency => 
      cryptocurrency.name.toLowerCase().includes(search.toLowerCase()))
      console.log(filteredCrypto)

     return filteredCrypto
    }



  return (
    <div className="cryptocurrency-app">
      <Form setSearch={setSearch}/>  

      {filteredCrypto.forEach(crypto => {   
        return (
          <Show  image={crypto.image} name={crypto.name} symbol={crypto.symbol} price={crypto.current_price} />
        )
      })
     }
    </div>
  );
}

export default App;
